--Restrict and Sort Data:
--1. where clause:  (is used to fetch the data based on certain condition)
select * from emp_new where id=101;

--2. order by: (is used to sort data in asc or desc order):
select * from emp_new order by id desc;

--3. Arithmetic operators: (+,-,*,/)
--(+): is used to add numbers:
select 10 + 5 + 5 as result from dual;

--(-): We can subtract the values:
select 15 - 5 - 5 as result from dual;

--(*):  to perform multiplication:
select 5 * 10 as result from dual;

--(/):  to perform division:
select 50 / 10 as result from dual;
select 10 +  nvl(null,0) as result from dual;

--BODMAS rule: (Bracket, Off, Division, mul, add, sub):
select 10 + 20 * 3 - (8 / 2 + 6) * 2 as result from dual;
--Inner bracket: 8 / 2= 4
--Inner addition: 4 + 6 = 10
--Multiply: 10 * 2 = 20
--Main: 10 + 20 * 3 - 20
--Multiply: 20 * 3 = 60
--Final: 10 + 60 - 20= 70 - 20 = 50

--Comparison and relational operators:
--1. = equals to
select * from emp_new where salary = 50000;

--2. != or <>
select * from emp_new where salary != 50000;

--3. > (greater than),  < (less than)
select * from emp_new where salary > 30000;
select * from emp_new where salary < 30000;

--4. >= (greater than equals to) , <= (less than equals to)
select * from emp_new where salary >= 30000;
select * from emp_new where salary <= 30000;

--LOGICAL operators:
--(AND, OR, NOT)
--1. AND: All conditions should be true.
select * from emp_new where id =101 AND name='Rahul' and salary=50000;

--2. OR: At least one condition must be true
select * from emp_new where id=101 OR name='Sahil';

--3. NOT: Reverses the condition(true becomes false)
select * from emp_New where not salary=50000 ;

