-- DQL (Data Query Language) : is used to fetch the data from table-- SELECT

select * from emp_master;

--Operators that can be used with select statements:

--1. where clause: (to give any specific condition for data retrieval)
select * from emp_master where emp_name='D';

--2. order by: (to arrange the data in specific order asc or desc)
select * from emp_master order by emp_name asc;

--3. LIKE (to fetch the data based on any given pattern)
select * from emp_master where emp_name like '% Gupta';

--4. DISTINCT: (is used to fetch the unique records and it discards the duplicate entry in output)
select distinct emp_id, emp_name, salary, department from emp_master;

--5. BETWEEN (to view the data between given range)
select * from emp_master where salary between 10000 and 13000;

--6. IN (is used to fetch the record with the matching values)
select * from emp_master where salary in (10000,12000);

--7. IS NULL (to check if the column value is null or not)
select * from emp_master where salary is null;


update emp_master set emp_name ='Anuj Gupta' where emp_id=2;

--How to backup any table:
create table emp_bkp as select * from emp1;

--Table and Column comment:

--1. Comment on Table- adds description for entire table:
select * from emp_bkp;
comment on table emp_bkp is 'It is the backup table for emp1 table';
select * from user_tab_comments where table_name ='EMP_BKP';

--2. Comment on Column- adds description for a specific column
select * from emp_bkp;
comment on column emp_bkp.gender is 'It will store user gender info.';
select * from user_col_comments where table_name ='EMP_BKP';

--Alter, Update: (revision)
--Alter -- DDL (auto committed)
select * from emp_bkp;
desc emp_bkp;

--Alter
alter table emp_bkp add emp_name varchar(40);
alter table emp_bkp drop column emp_name;
alter table emp_bkp rename column gender to emp_gender;
alter table emp_bkp modify emp_gender varchar2(5);

--update (DML):
select * from emp_bkp;
desc emp_bkp;
update emp_bkp set emp_name='Aman', emp_id=1;

commit;

--3. DQL/DRL: (Data Query Language/ Data Retrieval Language)
--1. SELECT (To fetch the data from DB table/object)
desc emp_master;
select * from emp_master;

--Operators/Clauses: a. where clause
select * from emp_master where emp_id=2;

--b. order by: (to sort data in asc or desc order):
