--c. distinct keyword: (used to print unique data from table)
select distinct emp_id, emp_name, emp_gender from emp_master;

--d. like: (to fetch the data based on specific pattern)
select * from emp_master where emp_name like 'R% Gupta';

--e. IN operator: (to fetch the data based given value in operator)
select * from emp_master where emp_name in ('Rohit Gupta');

--f. between: (to fetch the data between given range)
select * from emp_master where emp_id between 1 and 3;

--g. is null: (used to fetch null record from table)
select * from emp_master where emp_gender is null;
select * from emp_master where emp_gender is not null;

--h. =,>,<
select * from emp_master where emp_id >= 1;
select * from emp_master where emp_id <= 6;

--DML (Merge): Merge is used when we want to update a row if its exists or
--insert a new row if its does not exist.

create table emp_main
(
  id number(5),
  name varchar(50),
  salary number(8)
);

create table emp_new
(
  id number(5),
  name varchar(50),
  salary number(8)
);

insert into emp_main values (101, 'Rahul',30000);
insert into emp_main values (102, 'Sneha',35000);

insert into emp_new values (101, 'Rahul',40000);
insert into emp_new values (103, 'Amit',28000);

select * from emp_new;

--What we need:
--if emp already exist --> Update query
--if emp does not exist --> insert query

merge into emp_main m
using emp_new n
on (m.id=n.id)
when matched then
update set m.salary=n.salary -- update exisitng employee

when not matched then
insert (id, name, salary) -- insert new employee data
values (n.id,n.name,n.salary);

select * from emp_main;

--TCL (Transaction control language): is used to control changes in database. It
--decides whether our changes should be saved or undone.

--COMMIT: is used to save the changes permanently in db memory
--DDL (CREATE, ALTER, RENAME, DROP, TRUNCATE) -- AUTO COMMITTED
--DML (INSERT, UPDATE, DELETE, MERGE) -- We need to commit or rollback it to save or
--undo the changes

insert into emp_new values (104,'Rohan', 40000);
insert into emp_new values (105,'Ram', 30000);
insert into emp_new values (106,'Shyam', 41000);
commit;

--ROLLBACK: to undio the changes before commit.
insert into emp_new values (107,'Ajay', 25000);
insert into emp_new values (108,'Avinash', 50000);
insert into emp_new values (109,'Gautam', 40000);
rollback;

select * from emp_new;

--SAVEPOINT: Marks a point within a transaction to which you can roll back.

INSERT INTO emp_new VALUES (107, 'Ajay',45000);
SAVEPOINT sp1;

INSERT INTO emp_new VALUES (108, 'Gautam',35000);
savepoint sp2;

DELETE FROM emp_new WHERE id = 101;

-- Rollback to a savepoint (only undoes statements after sp1)
ROLLBACK TO sp2;

-- Commit remaining changes
COMMIT;

--DML (Merge): Merge is used when we want to update a row if its exists or
--insert a new row if its does not exist.

--using below tables on merge
--emp_main
--emp_new

create table emp_main
(
  id number(5),
