--TCL (Transaction Control Language): It is used to save or revrt back the transaction operations in db memory.

--DDL : this are auto-committed (create, alter, drop, truncate, rename)
--DML : (insert, update, delete, merge)

-- 1. COMMIT: is used to save the operation result permanently in db memory
select * from emp_new;

insert into emp_new values (104,'Rohit',35000);
insert into emp_new values (105,'Aman',45000);
commit;

select * from emp_new;

-- 2. ROLLBACK: is used to revert back/undo the changes/operation result

insert into emp_new values (106,'Sneha',25000);
insert into emp_new values (107,'Sahil',15000);

rollback;

-- 3. SAVEPOINT: Marks a point within a transaction to which you can roll back.

INSERT INTO EMP_NEW VALUES (106, 'Sahil', 30000);
SAVEPOINT sp1;

INSERT INTO emp_new VALUES (107, 'Sneha',40000);
SAVEPOINT sp2;

DELETE FROM emp_new WHERE id = 101;

-- Rollback to a savepoint (only undoes statements after sp1)
ROLLBACK TO sp1;

-- Commit remaining changes
COMMIT;

