--3. Arithmetic operators: (+,-,*,/)
--(+): is used to add numbers:
select 10 + 5 + 5 as result from dual;

--(-): We can subtract the values:
select 15 - 5 - 5 as result from dual;

--(*):  to perform multiplication:
select 5 * 10 as result from dual;

--(/):  to perform division:
select 50 / 10 as result from dual;
select 10 +  nvl(null,0) as result from dual;

--BODMAS rule: (Bracket, Off, Division, mul, add, sub):
select 10 + 20 * 3 - (8 / 2 + 6) * 2 as result from dual;
--Inner bracket: 8 / 2= 4
--Inner addition: 4 + 6 = 10
--Multiply: 10 * 2 = 20
--Main: 10 + 20 * 3 - 20
--Multiply: 20 * 3 = 60
--Final: 10 + 60 - 20= 70 - 20 = 50

