--Constraints in SQL: Rules apply on table columns to protect data and avoid mistakes.
--They ensure that the data is unique, not missing and correctly linked between tables.

--1. UNIQUE : to store the value in column which must be unique but can be null
create table users
(
user_id number,
email varchar2(300) unique
);

desc users;
select * from users;
insert into users values (1, 'abc@gmail.com');
insert into users values (2, 'abcd@gmail.com');
insert into users values (3,null);
--No two users can have the same email
--Email can be NULL

--2. NOT NULL: Column must have a value, it cannot be empty.
alter table users modify user_id number not null;
insert into users values (null, 'abcdef@gmail.com');

--3. PRIMARY KEY: A column that uniquely identifies each row:
--Cannot be NULL
--Cannot have duplicate values (Unique values only)
--Combination of Unique + NOT NULL constraints

--to declare existing column as primary key
alter table users add constraint pk_users primary key (user_id);

--to remove exisiting primaty key from table
alter table users drop constraint SYS_C007075;

--adding and new column and declaring it as primary key:
alter table users add phone number(10);
alter table users add constraint pk_phone primary key (phone);
insert into users values (6, 'abcui@gmail.com', 7748503253);

--4. CHECK: Used to restrict allowed values. We define a define.
select * from users;
alter table users add salary number;
alter table users add constraint chk_salary check (salary>0);

update users set status='Hired';
insert into users values (7, 'abcuid@gmail.com', 7748505853, 3000);

--5. DEFAULT: If no value is entered, SQL will insert a default value automatically.
alter table users add status  varchar2(100);
