--   1.  Pre defined functions : Its of two types:
--  a. Single row functions: work on one row at a time.
--    Types: 1. Char functions:
--    1.1 UPPER(): to convert char data in upper case
    select upper('sql') from dual;
    select upper(name), e.*  from emp e;
--    1.2 LOWER(): to convert char data in lower case
    select lower('SQL') from dual;
    select lower(name),  emp_id, salary, manager_id from emp ;
--    1.3 SUBSTR(): to fetch the substring from original string
    select substr('Oracle', 1,3) from dual;
    select emp_id, name,substr(name,1,3), salary, manager_id from emp;
--    1.4 LENGTH(): to find the length of char data
    select length('Hello World') from dual;
    select emp_id, name,length(name), salary, manager_id from emp;

--  Types: 2. Number functions:     
--2.1. round(): select round(15.6789, 2) from dual;
select emp_id, name, salary, round(salary, 2), manager_id from emp;

--2.2. trunc(): select trunc(15.6789,1) from dual;
select emp_id, name, salary, trunc(salary, 1), manager_id
from emp;

--2.3. mod(): select mod(10,3) from dual;
select mod(emp_id,2), name, salary, manager_id
from emp;
--odd or even emp records
select * from emp where mod(emp_id,2)=1;

