--Joins in SQL:  is used to combine data from two tables based on a related column.
--1. Inner Join: shows only matching rows from both tables.
select * from marks;
create table students
(
roll_no number,
name varchar2(100)
);

create table marks
(
roll_no number,
marks number
);
desc marks;

insert into students values (1,'Rahul');
insert into students values (2,'Ram');
insert into students values (3,'Aman');
insert into students values (4,'Rohan');

select * from students;

insert into marks values (1,90);
insert into marks values (2,65);
insert into marks values (5,75);

select s.roll_no, s.name, m.marks
from students s inner join marks m
on s.roll_no=m.roll_no;

select * from students s inner join marks m on s.roll_no=m.roll_no;

--2. Left Join (Left Outer Join): shows all rows from left table and matching rows
--from right table
select * from marks;

select s.roll_no, s.name, m.marks
from students s left outer join marks m 
on s.roll_no=m.roll_no;

select * from students s left join marks m on s.roll_no=m.roll_no;;

--3. Right Join (Right Outer Join): shows all rows from right table and matching 
--rows from left table:

select students.roll_no, students.name, marks.marks
from marks right outer join students 
on students.roll_no=marks.roll_no;

--4. Full Outer Join: shows all rows from both tables, matching or not.

select *
from students s full outer join marks m
on s.roll_no=m.roll_no;

