--Aggregate functions: sum, min, max, count, avg
--Group by clause
--Having clause

--Group by clause: is used to group rows that have the same values in one or more
--columns and then we apply aggegate functions on each group.

desc sales;
create table sales
(
product varchar2(100),
amount number,
city varchar2(100)
);


insert into sales  values ('Mobile', 12000, 'Pune');
insert into sales values ('Laptop', 40000, 'Pune');
insert into sales values ('Mobile', 15000, 'Mumbai');
insert into sales values ('Laptop', 35000, 'Mumbai');

select * from sales;

--to find total sales per product
select product, sum(amount) as total_sales from sales group by product;

--Here we have grouped all 'Mobile' together and all 'Laptop' together.
select * from sales;
--Total sales in each city
select city, sum(amount) as total_sales from sales group by city;

--total sales per city per product
select city, product, sum(amount) as total_sales from sales group by city, product;

--having clause: is used to filter grouped (aggregated) data.
--it works after group by and is mainly used with aggregate functions like count, sum, min, max, etc

--Where: filter the individual row.
--having clause: filters the group.

--show products whose total sales amount is greater than 50000
select product, sum(amount) as total_sales from sales group by product
having sum(amount) > 50000;

--show products that are sold more than one time
select product, count(*) as product_count from sales group by product
having count(*) > 5;

