--Corelated Subquery: is a subquery that depends on the outer query.
--It runs once for each row processed by the outer query.

--Basic Syntax:

select * from outer_table o
where column >
(
select agg_fun(column) from inner_table where inner_table.key=o.key
);
--highest paid employee per department:
select * from employees;

select * from employees o
where salary =
(
select max(salary)
from employees where dept=o.dept
);


--orders whose amount is greater than avg order amount of that customer
select * from orders o where amount >
(
select avg(amount) from orders where customer_name=o.customer_name
);

