--6. COMPOSITE KEY: A primary key made of 2 or more columns together.
--dropping the exisiting primary key:
alter table users drop constraint PK_USERID;
--creating a composite key for two columns on existing table
alter table users add constraint ck_userid_email primary key (user_id, email);
--applying composite key contraint while creating table:
create table users
(user_id number,
email varchar2(100),
salary number check (salary >0), -- check constraint
status varchaar2(50) default 'Pending', -- default constraint
constraint com_key primary key(user_id, email) --composite key
);

--Column level and Table level constraints:
--Column level constraint: applied on single column
--Table level constraint: applied on multiple columns
create table users
(user_id number,
email varchar2(100),
salary number check (salary >0), -- check constraint -- column level constraint
status varchaar2(50) default 'Pending', -- default constraint  -- column level constraint
constraint com_key primary key(user_id, email) --composite key -- table level constraint
);

